using NUnit.Framework;
using Shared;

namespace Test.CheckKeywordsTests
{
    public class StrCheckTypeTest
    {
        [TestCase("{}")]
        [TestCase("[]")]
        [TestCase("{\"a\":{}}")]
        [TestCase("{\"a\": {}}")]
        [TestCase("{\"a\":null}")]
        [TestCase("{\"a\": null}")]
        [TestCase("{\"a\":{\"b\":123}}")]
        [TestCase("{\"a\":{\"b\": 123}}")]
        public void WhenInputJsonReturnTrue(string jsonStr)
        {
            var result = CheckKeywords.GetCheckType(jsonStr);

            Assert.IsTrue(result==EnumCheckType.Json);
        }

        [TestCase("")]
        [TestCase("{")]
        [TestCase("{'}")]
        [TestCase("{'a':{}}")]
        [TestCase("{'a':''}")]
        [TestCase("{'a\": {}}")]
        [TestCase("{\"a': {}}")]
        [TestCase("{\"a\"}")]
        [TestCase("{123}")]
        public void WhenInputInvalidJsonReturnFalse(string jsonStr)
        {
            var result = CheckKeywords.GetCheckType(jsonStr);

            Assert.IsFalse(result == EnumCheckType.Json);
        }

